mapper.select:
SELECT parent_id, COUNT(*) as reply_count FROM comments WHERE parent_id IN
#{id}
AND status = 1 GROUP BY parent_id

SELECT c1.* FROM comments c1 INNER JOIN ( SELECT parent_id, MAX(create_time) as max_time FROM comments WHERE parent_id IN
#{id}
AND status = 1 GROUP BY parent_id ) c2 ON c1.parent_id = c2.parent_id AND c1.create_time = c2.max_time
